                  {authorBio && <p className="text-gray-600">{authorBio}</p>}
                </div>
              </div>
            </div>
          </article>

          {relatedPosts.length > 0 && (
            <div className="mt-16">
              <SmartHeading level={2} className="text-2xl font-bold mb-8">مقالات ذات صلة</SmartHeading>
