import { Metadata } from 'next';
import Link from 'next/link';
import Image from 'next/image';
import { format, parseISO } from 'date-fns';
import { enUS } from 'date-fns/locale';

// Utilities
import { getAllPosts } from '@/lib/posts';

// Components
import YearNavigation from '@/components/blog/YearNavigation';
import BlogCardGrid from '@/components/blog/BlogCardGrid';
import Container from '@/components/ui/Container';
import SectionHeading from '@/components/ui/SectionHeading';

// Types
export interface PostPreview {
  slug: string;
  title: string;
  date: string;
  excerpt: string;
  coverImage: string;
  author: {
    name: string;
    picture: string;
    slug: string;
  };
  categories: string[];
  tags: string[];
}

interface YearPageProps {
  params: {
    year: string;
  };
}

// Generate static params for the years
export async function generateStaticParams() {
  return [
    { year: '2023' },
    { year: '2024' },
    { year: '2025' },
  ];
}

// Generate metadata for the page
export async function generateMetadata({ params }: YearPageProps): Promise<Metadata> {
  const { year } = params;

  return {
    title: `${year} Blog Archive | UNEOM Corporate Uniforms`,
    description: `Browse all UNEOM articles from ${year} about corporate uniforms, workwear, and professional clothing solutions for Saudi businesses.`,
    alternates: {
      canonical: `https://uneom.com/blog/${year}`,
      languages: {
        'ar': `https://uneom.com/ar/blog/${year}`,
        'en': `https://uneom.com/blog/${year}`,
      },
    },
    openGraph: {
      title: `${year} Blog Archive | UNEOM Corporate Uniforms`,
      description: `Browse all UNEOM articles from ${year} about corporate uniforms, workwear, and professional clothing solutions for Saudi businesses.`,
      url: `https://uneom.com/blog/${year}`,
      type: 'website',
      images: [
        {
          url: 'https://uneom.com/images/blog/archive-cover.jpg',
          width: 1200,
          height: 630,
          alt: `UNEOM Blog Archive ${year}`,
        }
      ],
    }
  };
}

export default async function YearArchivePage({ params }: YearPageProps) {
  const { year } = params;
  const yearNum = parseInt(year);

  // Prepare month names for display
  const monthNames = Array.from({ length: 12 }, (_, i) => 
    format(new Date(yearNum, i, 1), 'MMMM', { locale: enUS })
  );

  // Get all posts for this year
  const allPosts = await getAllPosts();
  const yearPosts = allPosts.filter(post => {
    const postDate = parseISO(post.date);
    return postDate.getFullYear() === yearNum;
  });

  // Group posts by month
  const postsByMonth = Array.from({ length: 12 }, (_, i) => {
    const monthIndex = i;
    const monthPosts = yearPosts.filter(post => {
      const postDate = parseISO(post.date);
      return postDate.getMonth() === monthIndex;
    });

    return {
      monthIndex,
      monthName: monthNames[i],
      posts: monthPosts.sort(
        (a, b) => new Date(b.date).getTime() - new Date(a.date).getTime()
      ),
    };
  });

  // Filter out months with no posts
  const monthsWithPosts = postsByMonth.filter(month => month.posts.length > 0);

  // Get previous and next year links
  const prevYear = (yearNum - 1).toString();
  const nextYear = (yearNum + 1).toString();

  // Only show next year link if it's not after current year + 1
  const currentYear = new Date().getFullYear();
  const showNextYear = yearNum < currentYear + 1;

  return (
    <div className="blog-archive-page py-10 md:py-16">
      <Container>
        {/* Page Header */}
        <header className="text-center mb-12">
          <SectionHeading>
            {year} Archives
          </SectionHeading>
          <p className="text-lg text-gray-600 dark:text-gray-300 mt-4 max-w-3xl mx-auto">
            Browse all our articles from {year} about uniforms, workwear, and corporate clothing solutions.
          </p>

          {/* Year Navigation */}
          <div className="mt-8">
            <YearNavigation currentYear={year} availableYears={['2023', '2024', '2025']} />
          </div>
        </header>

        {/* Year Navigation */}
        <div className="flex justify-between mb-10">
          {yearNum > 2023 && (
            <Link href={`/blog/${prevYear}`} className="text-primary hover:underline">
              ← {prevYear} Archives
            </Link>
          )}

          <Link href="/blog" className="text-primary hover:underline">
            View All Blog Posts
          </Link>

          {showNextYear && (
            <Link href={`/blog/${nextYear}`} className="text-primary hover:underline">
              {nextYear} Archives →
            </Link>
          )}
        </div>

        {/* Month Quick Navigation */}
        {monthsWithPosts.length > 0 && (
          <div className="mb-12">
            <h2 className="text-xl font-bold mb-4">Browse {year} by Month</h2>
            <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
              {monthsWithPosts.map(month => (
                <Link 
                  key={month.monthIndex}
                  href={`/blog/${year}/${month.monthIndex + 1}`}
                  className="bg-gray-100 dark:bg-gray-800 hover:bg-primary hover:text-white dark:hover:bg-primary rounded-lg p-3 text-center transition-colors"
                >
                  {month.monthName} ({month.posts.length})
                </Link>
              ))}
            </div>
          </div>
        )}

        {/* Posts by Month */}
        {monthsWithPosts.length > 0 ? (
          <div className="space-y-16">
            {monthsWithPosts.map(month => (
              <section key={month.monthIndex} className="month-section">
                <div className="flex items-center justify-between border-b border-gray-200 dark:border-gray-700 pb-2 mb-6">
                  <h2 className="text-2xl font-bold">{month.monthName} {year}</h2>
                  <Link 
                    href={`/blog/${year}/${month.monthIndex + 1}`}
                    className="text-primary hover:underline text-sm"
                  >
                    View All {month.monthName} Posts →
                  </Link>
                </div>

                <BlogCardGrid posts={month.posts} locale="en" />
              </section>
            ))}
          </div>
        ) : (
          <div className="text-center py-20 bg-gray-50 dark:bg-gray-800 rounded-lg">
            <h2 className="text-2xl font-bold mb-4">No articles found for {year}</h2>
            <p className="text-gray-600 dark:text-gray-300 mb-6">
              We couldn't find any articles published during this year.
            </p>
            <Link href="/blog" className="btn btn-primary">
              Browse all articles
            </Link>
          </div>
        )}

        {/* Archive Navigation */}
        <div className="border-t border-gray-200 dark:border-gray-700 pt-8 mt-16">
          <h2 className="text-xl font-bold mb-4">Blog Archive Navigation</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <h3 className="font-medium mb-2">Years</h3>
              <ul className="space-y-1">
